package noPattern;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int cnt=0;
		for(int i=1;i<=5;i++) {
	      	  
	      	  for(int j=1;j<=i;j++) {
	      		      cnt++;
					 System.out.print(cnt+" ");
					 
				 }
	      	System.out.println(" ");
		}
	}

}
