package starPattern;

public class ReverseTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
/*	 1] * * * *     * * * *
		* * *         * * *
		* *             * *
		*                 *
*/
		
           for(int i=1;i<=4;i++) {
			 
			 for(int j=4;j>=i;j--) {
				
					 
					 System.out.print("*");
					 System.out.print(" ");
				 
				
			 }
			 System.out.println();
				 
		 }

       }
     
}
